import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Premium from "./pages/Premium";   // ✅ Premium page
import Register from "./pages/Register"; // ✅ Register page
import Login from "./pages/Login";       // ✅ Login page

// Home Page
function Home({ status }) {
  const Card = ({ to, title, subtitle }) => (
    <Link
      to={to}
      style={{
        display: "block",
        textDecoration: "none",
        border: "1px solid #e5e7eb",
        borderRadius: 12,
        padding: 16,
        minWidth: 220,
        boxShadow: "0 2px 10px rgba(0,0,0,0.05)",
      }}
    >
      <div style={{ fontSize: 18, fontWeight: 700, marginBottom: 6, color: "#111827" }}>
        {title}
      </div>
      <div style={{ fontSize: 14, color: "#4b5563" }}>{subtitle}</div>
    </Link>
  );

  return (
    <div style={{ fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, sans-serif" }}>
      <header style={{ padding: "24px 20px", borderBottom: "1px solid #e5e7eb" }}>
        <h1 style={{ margin: 0, fontSize: 28 }}>Password Utilities — Mark 0.2</h1>
        <p style={{ margin: "6px 0 0", color: "#6b7280" }}>
          Default landing page • Status: <strong>{status}</strong>
        </p>
      </header>

      <main style={{ padding: 20 }}>
        <section style={{ margin: "18px 0 8px" }}>
          <h2 style={{ margin: 0, fontSize: 20 }}>Tools & Pages</h2>
          <p style={{ marginTop: 6, color: "#6b7280" }}>
            Choose a tool or page to continue. (Features will be added in the next steps.)
          </p>
        </section>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(240px, 1fr))",
            gap: 16,
            alignItems: "stretch",
          }}
        >
          <Card to="/generator" title="Password Generator" subtitle="Create strong, memorable passwords" />
          <Card to="/strength" title="Strength Checker" subtitle="Analyze password strength" />
          <Card to="/break" title="Break-Time Estimator" subtitle="Estimate time to crack" />
          <Card to="/premium" title="Premium: RefGenPass" subtitle="Reference-based generator (login required)" />
          <Card to="/login" title="Login" subtitle="Access premium features" />
          <Card to="/register" title="Register" subtitle="Create an account" />
        </div>
      </main>

      <footer style={{ padding: 20, borderTop: "1px solid #e5e7eb", color: "#6b7280" }}>
        © {new Date().getFullYear()} Mark 0.2
      </footer>
    </div>
  );
}

// Simple placeholder component
function Placeholder({ title, note }) {
  return (
    <div style={{ padding: 24, fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, sans-serif" }}>
      <h1 style={{ marginTop: 0 }}>{title}</h1>
      <p style={{ color: "#6b7280" }}>{note || "Coming soon."}</p>
      <Link to="/" style={{ color: "#2563eb" }}>← Back to Home</Link>
    </div>
  );
}

function App() {
  const [status, setStatus] = useState("Connecting...");

  useEffect(() => {
    fetch("/api/ping")
      .then((res) => res.text())
      .then((data) => setStatus(data))
      .catch(() => setStatus("Connection failed"));
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        {/* Home Page */}
        <Route path="/" element={<Home status={status} />} />

        {/* Placeholders for other routes */}
        <Route path="/generator" element={<Placeholder title="Password Generator" />} />
        <Route path="/strength" element={<Placeholder title="Strength Checker" />} />
        <Route path="/break" element={<Placeholder title="Break-Time Estimator" />} />

        {/* ✅ Real Premium page */}
        <Route path="/premium" element={<Premium />} />

        {/* ✅ Real Register page */}
        <Route path="/register" element={<Register />} />

        {/* ✅ Real Login page */}
        <Route path="/login" element={<Login />} />

        {/* Fallback */}
        <Route path="*" element={<Placeholder title="404 — Not Found" />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
